stopwords = stopwords + stopwords_more
output = {}


flag = False
for index in range(len(df)):
    content = df['content'].iloc[index]
    topic = df['topic'].iloc[index]
    content = content.replace('\n', ' ')
    if topic not in output:
        output[topic] = {}
        pass
    while True:
        index_http = content.find('http')
        if index_http == -1:
            break
        else:
            flag = True
            index_space = content.find(' ', index_http)
            if index_http == 0:
                content = content[index_space + 1:]
            elif index_space == -1:
                content = content[:index_http - 1]
            else:
                content = content[:index_http - 1] + content[index_space + 1:]
                pass
            pass
        pass
    for punctuation in punctuations:
        content = content.replace(punctuation, ' ')
        pass
    content = content.split(' ')
    for word in content:
        if not word.isalpha():
            continue
            pass
        word = word.lower()
        if word in output[topic]:
            output[topic][word] += 1
        else:
            output[topic][word] = 1
            pass
        pass
    # if flag:
    #     break
    #     pass
    pass

word_counts = {}
for topic in output.keys():
    for word in output[topic].keys():
        count = output[topic][word]
        if word not in word_counts:
            word_counts[word] = count
        else:
            word_counts[word] += count
            pass
        pass
    pass

output_final = {}
for topic in output.keys():
    output_final[topic] = {}
    for word in output[topic].keys():
        if word_counts[word] < 10 or word in stopwords:
            continue
        else:
            output_final[topic][word] = output[topic][word]
            pass
        pass
    pass

with open('word_counts_more.json', 'w') as f:
    json.dump(output_final, f)
